function main(){get=(i=>document.querySelector(i)),getAll=(i=>document.querySelectorAll(i)),loadPageData().then(i=>{dom={linklist:get(".linklist"),content:get("#content")},buildLinks(i.sections),buildSections(i.sections),buildExtLinks(i.ext),buildDevIcons(i.icons)})}async function loadPageData(){let i=await fetch("./data/data.json");return await i.json()}function buildLinks(i){for(let e of i)dom.linklist.appendChild(Builder.build("li",{children:[Builder.build("a",{href:"#"+e.section+"-section",inner:e.title,"data-text":e.title})]}))}async function buildSections(i){for(let e of i)switch(e.type){case"pg":buildPgSection(e);break;case"ls":buildLsSection(e);break;case"rs":buildRsSection(e)}}function buildExtLinks(i){let e=get(".ext-icon-list");for(let l in i)e.appendChild(Builder.build("a",{class:"bounce",href:i[l].link,target:"_blank",children:[Builder.build("i",{class:"devicon-"+i[l].icon})]}))}function buildDevIcons(i){let e=get(".lang-icon-list");for(let l of i.languages)e.appendChild(Builder.build("span",{children:[Builder.build("i",{class:"devicon-"+l+"-plain","data-text":l})]}));let l=get(".tool-icon-list");for(let e of i.tools)l.appendChild(Builder.build("span",{children:[Builder.build("i",{class:"devicon-"+e+"-plain","data-text":e})]}))}function buildPgSection(i){dom.content.appendChild(Builder.build("div",{id:i.section+"-section",class:"section",children:[Builder.build("h2",{class:"section-title",inner:i.title}),Builder.build("p",{class:"section-body",inner:i.body})]}))}function buildLsSection(i){dom.content.appendChild(Builder.build("div",{id:i.section+"-section",class:"section",children:[Builder.build("h2",{class:"section-title",inner:i.title}),Builder.build("ul",{class:"section-body-list",children:i.body.map(i=>Builder.build("li",{class:"section-list-item",children:[i.header?Builder.build("h4",{class:"list-item-header",inner:i.header}):null,i.subheader?Builder.build("h4",{class:"list-item-subheader",inner:i.subheader}):null,i.subsubheader?Builder.build("h6",{class:"list-item-subsubheader",inner:i.subsubheader}):null,i.main?Builder.build("p",{class:"list-item-main",inner:i.main}):null].filter(i=>null!==i)}))})]}))}function buildRsSection(i){window.screen.width>980&&dom.content.appendChild(Builder.build("div",{id:i.section+"-section",class:"section mobile-hide",children:[Builder.build("h2",{class:"section-title",inner:i.title}),Builder.build("iframe",{src:i.file+"#toolbar=0",class:"section-body-iframe",id:"pdfFrame"})]})),dom.content.appendChild(Builder.build("div",{id:i.section+"-section",class:"section mobile",children:[Builder.build("h2",{class:"section-title",children:[Builder.build("a",{href:i.file,inner:"Download Resume PDF",download:"whitson_resume_23.pdf"})]})]}))}document.addEventListener("DOMContentLoaded",main);